package week1.day2;

public class PrintEvenNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int number=20;
		//for loop & if condition
		
		for (int i = 0; i <=number; i++) {
			if(i%2==0) {
				
				System.out.println("Even Number"+ i);
			}
				
			
		}
	}

}
